من یه فایل باینری تعریف کردم و بایت بایت تو فایل میریزم
void Huffman :: saveEncodedHuffmanStringToABinaryFile (string str, string resultFileName)
{
    ofstream myFile;
    myFile.open (resultFileName, ios::out | ios::binary);
    int length = 8;
    for (int i = 0; i < str.size() / length; i ++ )
    {      
        char* acc = new char [1];
        length = str.copy(acc, length, i);
        acc[length] = '\0';
        bitset<8> bitset3(acc);
        myFile << bitset3;
        myFile.flush();
    }
    myFile.close();
}
